import React, { Component } from 'react'
import Header from './Components/Header';
import Navbar from './Components/Navbar';
import sendRequest from './request';
import './styles/App.css';


export default class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      data: null,
      breadcrumbs:null
    }
  }
  componentDidMount() {
    const query = `
    query{
      products(
        search: null
        sort: { position: ASC }
        currentPage: 1
        pageSize: 1
        filter: {
          custom_filter: [{ attribute: "entity_id", condition: { eq: "1958" } }]
        }
      ){
      items{
        name
        sku
        special_price 
        price{
        regularPrice{
          amount{
            currency
            value
          }
        }

      }

         breadcrumbs{
        category_name

      }

      media_gallery_entries{
        id
        thumbnail{
          url
        }

      }
          media_gallery{
        label
        url
      }

    }

    }
    }

    `
    sendRequest({
      query,
    }).then(res => {
      console.log(res);
      this.setState({
        data: res,
        breadcrumbs:res.data.products.items[0].breadcrumbs[0]
      })
    })
    // console.log(this.state.data.data);
  }

  render() {
// console.log(this.state.data);
    return (
      <div className="App">
          {
              !this.state.data ? (
                <p>Loading....</p>
              ) : (
                <>
                  <div className="header-section">


                <Header/>

                <Navbar/>
                </div>

                <div className="breadcrums">
                  <ul>
                    <li>
                      {}
                    </li>
                    <li></li>
                    <li></li>
                    <li></li>
                  </ul>
                </div>


                </>
              )
          }
      </div>




    );
  }
}
