import React, { Component } from 'react'
import Header from './Components/Header';
import sendRequest from './request';
import './styles/App.css';


export default class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      data: null
    }
  }
  componentDidMount() {
    const query = `
    query{
      products(
        search: null
        sort: { position: ASC }
        currentPage: 1
        pageSize: 1
        filter: {
          custom_filter: [{ attribute: "entity_id", condition: { eq: "1958" } }]
        }
      ){
      items{
        name
        sku
        special_price 
        price{
        regularPrice{
          amount{
            currency
            value
          }
        }

      }

         breadcrumbs{
        category_name

      }

      media_gallery_entries{
        id
        thumbnail{
          url
        }

      }
          media_gallery{
        label
        url
      }

    }

    }
    }

    `
    sendRequest({
      query,
    }).then(res => {
      console.log(res);
      this.setState({
        data: res
      })
    })
    // console.log(this.state.data.data);
  }

  render() {
// console.log(this.state.data);
    return (
      <div className="App">
        {
              !this.state.data ? (
                <p>Loading....</p>
              ) : (
                <div>

                <Header/>

        {
              this.state.data.products.items[0].media_gallery_entries.map((i,val) => {
                return(

                  <div>{i.id}Hello</div>

                )
              })
              }

                </div>
              )
        }
      </div>




    );
  }
}
