import React, { Component } from 'react'
import Header from './Components/Header';
import Navbar from './Components/Navbar';
import Slider from './Components/Slider';
import sendRequest from './request';
import './styles/App.css';


export default class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      data: null,
      breadcrumbs:[],
      images:[]
    }
  }
  componentDidMount() {
    const query = `
    query{
      products(
        search: null
        sort: { position: ASC }
        currentPage: 1
        pageSize: 1
        filter: {
          custom_filter: [{ attribute: "entity_id", condition: { eq: "1958" } }]
        }
      ){
      items{
        name
        sku
        special_price 
        price{
        regularPrice{
          amount{
            currency
            value
          }
        }

      }

         breadcrumbs{
        category_name

      }

      media_gallery_entries{
        id
        thumbnail{
          url
        }

      }
          media_gallery{
        label
        url
      }

    }

    }
    }

    `
    sendRequest({
      query,
    }).then(res => {
      console.log(res);
      this.setState({
        data: res,
        breadcrumbs:res.data.products.items[0].breadcrumbs,
        images:res.data.products.items[0].media_gallery_entries
      })
    })
    // console.log(this.state.data.data);
  }

  render() {
// console.log(this.state.data);
    return (
      <div className="App">
          {
              !this.state.data ? (
                <p>Loading....</p>
              ) : (
                <>
                  <div className="header-section">


                <Header/>

                <Navbar/>
                </div>

                <div className="breadcrumbs">
                      <div className="breadcrumbs-inner">
                      <ul>
                        <li>Home</li>
                        <div className='arrow'></div>
                        {
                          this.state.breadcrumbs.map((item)=>{
                            return(
                              <>
                              <li>{item.category_name}</li>
                              <div className='arrow'></div>
                              </>
                            )
                          })
                        }

                          <li style={{color:'#888'}}>{this.state.data.data.products.items[0].name}</li>
                      </ul>

                      </div>
                </div>

                <div className="product-content">

                </div>

                <Slider images={this.state.images}/>





                </>
              )
          }
      </div>




    );
  }
}
