export default class Slider extends Component {
  render() {

    const {images}=this.props;
    return(
     <>
       <Carousel className='main-slider'>

         {
           images.map((image)=>{
             return(

              <div className='img'>
                div.image-wrap
              <img src={image.thumbnail.url} alt=''/>

