import React, { Component } from 'react'
import Header from './Components/Header';
import Navbar from './Components/Navbar';
import Slider from './Components/Slider';
import sendRequest from './request';
import './styles/App.css';


export default class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      data: null,
      breadcrumbs:[],
      images:[],
      currency:null
    }
  }
  componentDidMount() {
    const query = `
    query{

      currency{
        available_currency_codes
        base_currency_code
        base_currency_symbol
        default_display_currecy_code
        default_display_currecy_symbol
        default_display_currency_code
        default_display_currency_symbol
        exchange_rates{
         currency_to
        rate
        }
      }

      products(
        search: null
        sort: { position: ASC }
        currentPage: 1
        pageSize: 1
        filter: {
          custom_filter: [{ attribute: "entity_id", condition: { eq: "1958" } }]
        }
      ){
      items{
        name
        sku
        special_price 
        price{
        regularPrice{
          amount{
            currency
            value
          }
        }

      }

         breadcrumbs{
        category_name

      }

      media_gallery_entries{
        id
        thumbnail{
          url
        }

      }
          media_gallery{
        label
        url
      }

    }

    }
    }

    `
    sendRequest({
      query,
    }).then(res => {
      // console.log(res);
      this.setState({
        data: res,
        breadcrumbs:res.data.products.items[0].breadcrumbs,
        images:res.data.products.items[0].media_gallery_entries,
        currency:res.data.currency
      })
    })

  }

  render() {

    return (
      <div className="App">
          {
              !this.state.data ? (
                <p>Loading....</p>
              ) : (
                <>
                  <div className="header-section">


                <Header/>

                <Navbar/>
                </div>

                <div className="breadcrumbs">
                      <div className="breadcrumbs-inner">
                      <ul>
                        <li>Home</li>
                        <div className='arrow'></div>
                        {
                          this.state.breadcrumbs.map((item)=>{
                            return(
                              <>
                              <li>{item.category_name}</li>
                              <div className='arrow'></div>
                              </>
                            )
                          })
                        }

                          <li style={{color:'#888'}}>{this.state.data.data.products.items[0].name}</li>
                      </ul>

                      </div>
                </div>

                <div className="product-content">

                 <div className="slider-img">
                <Slider images={this.state.images}/>
                </div>

                <div className="product-details">

                        <div className="heading">
                          <h1>{this.state.data.data.products.items[0].name}</h1>
                          <p>SKU :{this.state.data.data.products.items[0].sku}</p>
                        </div>

                        <div className="cart-price-section">

                       <div className="price-tags">
                         <div className='special-price'>
                            {this.state.currency.base_currency_symbol}{this.state.data.data.products.items[0].special_price}
                         </div>

                         <div className="original-price">
                         {this.state.currency.base_currency_symbol}{this.state.data.data.products.items[0].price.regularPrice.amount.value}
                         </div>

                         <div className="cart-details">
                           <div className="input">
                             <input type="number" value={va} className=''/>
                             <button className='increment'>+</button>
                             <button className='decrement'>-</button>
                           </div>
                         </div>


                       </div>



                        </div>



                </div>

                </div>





                </>
              )
          }
      </div>




    );
  }
}
