import React, { Component } from 'react'
import sendRequest from './request';
import './styles/App.css';


export default class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      data: []
    }
  }
  componentDidMount() {
    const query = `
    query _Welcome_to_Altair_G415 {
      products(
        search: null
        sort: { position: ASC }
        currentPage: 1
        pageSize: 1
        filter: {
          custom_filter: [{ attribute: "entity_id", condition: { eq: "1958" } }]
        }
      ){
      items{
        name
        sku
        special_price 
        price{
        regularPrice{
          amount{
            currency
            value
          }
        }


      }

         breadcrumbs{
        category_name

      }
          media_gallery{
        label
        url
      }

    }

    }
    }

    `
    sendRequest({
      query,
      variables: { url: "men.html" }
    }).then(res => {
      console.log(res);
      this.setState({
        data: (res.data.products.item[0])
      })
    })
  }

  render() {
    return (
      <div className="App">
        <h1>React and Graphql</h1>
        <p>Sample response</p>
        {
          !this.state.data ? (
            <p>Loading....</p>
          ) : (
            <div>
              {this.state.data}
            </div>
          )
        }
      </div>
    );
  }
}
